H^\circ_\mathrm{r} = \qty{-543}{\tfrac{\unit{kJ}}{\unit{mol}}} \\
    \ce{ 2 H2(g) + O2(g) &-> 2 H2O(g) }         && \Delta
H^\circ_\mathrm{r} = \qty{-484}{\tfrac{\unit{kJ}}{\unit{mol}}} \\
    \ce{ N2(g) + 3 H2(g) &-> 2 NH3(g) }         && \Delta
H^\circ_\mathrm{r} = \qty{-92}{\tfrac{\unit{kJ}}{\unit{mol}}}
