\section{Os mecanismos de reação}

\section{Combinação de Reações Elementares}

\begin{enumerate}
\tightlist
\item
  Etapa determinante na velocidade.
\item
  Aproximação do estado estacionário.
\item
  Aproximação do pré-equilíbrio.
\end{enumerate}

\subsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Determinar} a lei de velocidade para um mecanismo reacional.
\end{itemize}

\section{Reações em Cadeia}

\begin{enumerate}
\tightlist
\item
  Iniciação.
\item
  Propagação.
\item
  Término.
\item
  Reações radicalares.
\end{enumerate}

\subsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Determinar} a lei de velocidade para uma reação em cadeia.
\end{itemize}

