\paragraph{\texorpdfstring{De {\(\ln([\ce{A}]_t/[\ce{A}]_0) = -kt\)}.}{De \textbackslash ln({[}\textbackslash ce\{A\}{]}\_t/{[}\textbackslash ce\{A\}{]}\_0) = -kt.}}\label{de-lncea_tcea_0--kt.}}

