\section{Raio Atômico}

\begin{enumerate}
\tightlist
\item
  Raio covalente.
\item
  Raio de van der Waals.
\item
  Tendência no raio atômico.
\item
  Contração do Bloco \(d\).
\item
  Contração dos Lantanídeos.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Ordenar} átomos em função de seu raio.
\item
  \textbf{Explicar} a origem do efeito de Contração do Bloco \(d\).
\item
  \textbf{Explicar} a origem do efeito de Contração dos Lantanídeos.
\end{itemize}

\section{Raio Iônico}

\begin{enumerate}
\tightlist
\item
  Tendência no raio de cátions e ânions.
\item
  Raio de espécies isoeletrônicas.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Ordenar} átomos e íons em função de seu raio.
\end{itemize}

\section{Energia de Ionização}

\begin{enumerate}
\tightlist
\item
  Tendência da primeira energia de ionização.
\item
  \(n\)-ésima energia de ionização.
\item
  Energias de ionização anômalas entre \(s^2\) e \(s^2p^1\) (\(\ce{Be}\)
  e \(\ce{B}\); \(\ce{Al}\) e \(\ce{Mg}\))
\item
  Energias de ionização anômalas entre \(p^3\) e \(p^4\) (\(\ce{O}\) e
  \(\ce{N}\); \(\ce{S}\) e \(\ce{P}\)).
\item
  Teoria do Mar de Elétrons.
\item
  Condutores e isolantes.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Ordenar} átomos em função de sua primeira energia de
  ionização.
\item
  \textbf{Explicar} a origem de anomalias na energia de ionização.
\item
  \textbf{Determinar} o número de elétrons na camada de valência de uma
  espécie com base em suas energias de ionização.
\end{itemize}

\section{Afinidade Eletrônica}

\begin{enumerate}
\tightlist
\item
  Tendência da afinidade eletrônica.
\item
  Anomalia do flúor (\(\ce{Cl}\) e \(\ce{F}\)).
\item
  Afinidades eletrônicas anômalas entre \(p^2\) e \(p^3\) (\(\ce{C}\) e
  \(\ce{N}\); \(\ce{Si}\) e \(\ce{P}\)).
\item
  Afinidades eletrônicas negativas (\(\ce{O^-}\) e \(\ce{S^-}\)).
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Ordenar} átomos em função de sua afinidade eletrônica.
\item
  \textbf{Explicar} a origem de anomalias na afinidade eletrônica.
\end{itemize}

\section{Eletronegatividade}

\begin{enumerate}
\tightlist
\item
  Escala de Eletronegatividade de Mulliken:
  \(\chi = \frac{1}{2}(EI+AE)\)
\item
  Tendências na eletronegatividade.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Ordenar} átomos em função de sua eletronegatividade e
  polarizabilidade.
\end{itemize}

\section{Número de Oxidação}

\begin{enumerate}
\tightlist
\item
  Cálculo do número de oxidação.
\item
  Tendência no número de oxidação.
\item
  Efeito do Par-Inerte (\(\ce{In}\); \(\ce{Sn}\); \(\ce{Sb}\);
  \(\ce{Tl}\); \(\ce{Pb}\); \(\ce{Bi}\)).
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Calcular} o número de oxidação de um átomo em um composto.
\item
  \textbf{Explicar} a origem do Efeito do Par-Inerte.
\end{itemize}

\section{Relações Diagonais}

\begin{enumerate}
\tightlist
\item
  Origem das Relações Diagonais.
\item
  Relações Diagonais (\(\ce{Li}\) e \(\ce{Mg}\); \(\ce{Be}\) e
  \(\ce{Al}\); \(\ce{B}\) e \(\ce{Si}\)).
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Explicar} a origem das Relações Diagonais.
\end{itemize}

\section{Propriedades Físico-Químicas dos Elementos}

\begin{enumerate}
\tightlist
\item
  Propriedades físicas dos metais e ametais. \textbar{} Metais
  \textbar{} Ametais \textbar{} \textbar{} -\/- \textbar{} -\/-
  \textbar{} \textbar{} condutores elétricos \textbar{} isolantes
  elétricos \textbar{} \textbar{} condutores térmicos \textbar{}
  isolantes térmicos \textbar{} \textbar{} maleáveis \textbar{} não
  maleáveis \textbar{} \textbar{} dúcteis \textbar{} não dúcteis
  \textbar{} \textbar{} lustrosos \textbar{} não lustrosos \textbar{}
  \textbar{} sólidos \textbar{} sólidos, líquidos e gases \textbar{}
\item
  Propriedades químicas dos metais e ametais. \textbar{} Metais
  \textbar{} Ametais \textbar{} \textbar{} -\/- \textbar{} -\/-
  \textbar{} \textbar{} reagem com ácidos \textbar{} não reagem com
  ácidos \textbar{} \textbar{} formam óxidos básicos \textbar{} formam
  óxidos ácidos \textbar{} \textbar{} formam cátions \textbar{} formam
  ânions \textbar{} \textbar{} formam haletos iônicos \textbar{} formam
  haletos covalentes \textbar{}
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Comparar} propriedades físicas de elementos em função de sua
  posição na tabela periódica.
\item
  \textbf{Comparar} propriedades químicas de elementos em função de sua
  posição na tabela periódica.
\end{itemize}

