content.tex:
\section{Solubilidade em Nível Molecular}

\begin{enumerate}
\tightlist
\item
  Solvatação.
\item
  Interações soluto-solvente.
\item
  \emph{Semanhante-dissolve-semelhante}.
\item
  Espécies hidrofílicas e hidrofóbicas.
\item
  Micelas.
\item
  Surfactantes.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Comparar} a solubilidades com base na estrutura molecular.
\end{itemize}

\section{Limites da Solubilidades}

\begin{enumerate}
\tightlist
\item
  Soluções saturadas.
\item
  Solubilidade e temperatura.
\item
  Curvas de solubilidades.
\item
  Soluções supersaturadas.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Determinar} a solubilidade de uma substância a partir da curva
  de solubilidade.
\end{itemize}

\section{Solubilidade de Gases}

\begin{enumerate}
\tightlist
\item
  Lei de Henry: \[
   s = k_\text{H} P
   \]
\item
  Solubulidade de gases e temperatura.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Calcular} a solubilidade de um gás em um líquido a partir da
  Lei de Henry.
\end{itemize}

\section{Termodinâmica da Dissolução}

\begin{enumerate}
\tightlist
\item
  Entalpia rede.
\item
  Ciclo de Born-Haber.
\item
  Entalpia de hidratação.
\item
  Entalpia de dissolução.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Calcular} a entalpia de rede utilizando o ciclo de Born-Haber.
\item
  \textbf{Calcular} a entalpia de dissolução em função das entalpias de
  rede e de hidratação.
\end{itemize}

\section{Coloides}

\begin{enumerate}
\tightlist
\item
  Movimento Browniano.
\item
  Definição de coloides: \[
   \qty{1}{nm} < d < \qty{1}{\micro m}
   \]
\item
  Classificação de coloides.
\item
  Efeito Tyndall.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Identidicar} os tipos de coloides e suas propriedades.
\end{itemize}

