content.tex:
\section{Composição das Soluções}

\begin{enumerate}
\tightlist
\item
  Concentração mássica e molar.
\item
  Partes por milhão (\(\unit{ppm}\)) e bilhão (\(\unit{ppb}\)).
\item
  Molalidade.
\item
  Concentração de peróxido de hidrogênio em volumes: \[
   F = \dfrac{V_\text{CNTP}(\ce{O2})}{V} = \num{11,2} \cdot \dfrac{[\ce{H2O2}]}{\unit{mol.L^{-1}}}
   \]
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Relacionar} as unidade de concentração de soluções.
\item
  \textbf{Calcular} a concentração em volumes para uma solução de
  peróxido de hidrogênio.
\end{itemize}

\section{Operações com Soluções}

\begin{enumerate}
\tightlist
\item
  Diluição e secagem.
\item
  Misturas.
\item
  Balançoes materiais.
\item
  Balanço energético.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Calcular} a concentração final após uma operação em solução.
\end{itemize}

\section{Reações em Solução}

\begin{enumerate}
\tightlist
\item
  Estequiometria em solução.
\item
  Rendimento.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Calcular} a quantidade de produto formado em uma reação em
  solução.
\end{itemize}

\section{Soluções Iônicas}

\begin{enumerate}
\tightlist
\item
  Balanço de cargas.
\item
  Condutividade específica: \[
   \kappa =\dfrac{1}{\rho} = \dfrac{l}{RA}
   \]
\item
  Condutividade molar: \[
   \Lambda^\circ_\text{m} = \dfrac{\kappa}{c}
   \]
\item
  Lei de migração independente: \[
   \Lambda^\circ_\text{m} = \lambda^\circ_{+} + \lambda^\circ_{-}
   \]
\item
  Mobilidade iônica.
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Calcular} a concentração de íons em solução a partir da
  condutividade.
\item
  \textbf{Comparar} a condutividade de diferentes íons em solução.
\end{itemize}

\section{Absortividade das Soluções}

\begin{enumerate}
\tightlist
\item
  Espectrofotometria.
\item
  Absortividade molar.
\item
  Lei de Beer-Lambert: \[
   A = \log_{10}\dfrac{I_0}{I} = \epsilon L c
   \]
\end{enumerate}

\subsubsection{Habilidades}

\begin{itemize}
\tightlist
\item
  \textbf{Determinar} a concentração de íons em solução a partir de sua
  absortividade.
\end{itemize}

